import React, { useEffect, useEfffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Row,
  Col,
  ListGroup,
  Image,
  Form,
  Button,
  Card,
} from "react-bootstrap";
import Message from "../components/Message";
import { addToCart, removeFromCart } from "../actions/cartActions";

function CartScreen({ match, location, history }) {
  const productId = match.params.id;
  const qty = location.search ? Number(location.search.split("=")[1]) : 1;
  const dispatch = useDispatch();

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);


  const removeFromCartHandler =(id) => {
    dispatch(removeFromCart(id))
  }

  const checkoutHandler =() => {
      history.push('/login?redirect=shipping')
  }

  return (
    
    <Row>
      <Col md={8}>
        <h1>Тележка</h1>
        {cartItems.length === 0 ? (
          <Message>
            Ваша тележка пуста <Link to="/" style={{ textDecoration: 'none' , color:'red'}} >Go Back</Link>
          </Message>
        ) : (
          <ListGroup variant="flush">
            {cartItems.map((item) => (
              <ListGroup.Item key={item.product}>
                <Row>
                  <Col md={2}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>
                  <Col md={3}>
                    <Link to={`/product/${item.product}`} style ={{ textDecoration: 'none' }}>{item.name}</Link>
                  </Col>

                  <Col md={2}>${item.price}</Col>
                  <Col md={3}>
                    <Form.Select
                      as="select"
                      value={item.qty}
                      onChange={(e) => dispatch(addToCart(item.product, Number(e.target.value)))}
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </Form.Select>
                  </Col>

                  <Col md={1}>
                      <Button
                            type='button'
                            variant='light'
                            onClick={() => removeFromCartHandler(item.product)}
                        >
                            <i className="fas fa-trash"></i>
                        </Button>
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>
      <Col md={4}>
          <Card>
              <ListGroup className variant="flush">
                  <ListGroup.Item>
                      <h2>Subtotal ({cartItems.reduce((acc, item) => acc + item.qty, 0)})</h2>
                      ${cartItems.reduce((acc, item) => acc + item.qty * item.price, 0).toFixed(2)}
                  </ListGroup.Item>
              </ListGroup>
              <ListGroup.Item>
                <Row>
                  <Button
                    type="button"
                    className="btn-block"
                    disabled={cartItems.length === 0}
                    onClick={checkoutHandler}
                    >
                        Перейти к оформлению

                  </Button>
                  </Row>
              </ListGroup.Item>
          </Card>
      </Col>
    </Row>
    
  );
}

export default CartScreen;
